import logo from './logo.svg';
import Form from 'react-bootstrap/Form';
import { useState, React} from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const features = [
  "dorsal",
  "voice",
  "continuant",
]

const distinctiveFeatures = {
  "k" : {
    "dorsal" : "+",
    "voice" : "-",
    "continuant" : "-",
  },
  "d" : {
    "dorsal" : "-",
    "voice" : "+",
    "continuant" : "-",
  }
}

function App() {
  const [segmentSetA, setSegmentSetA] = useState([]);
  function handleSetChange(setInputText) {
    setSegmentSetA(setInputText.split(","));
  }
  return (
    <div className="App">
      <SegmentSelector changeHandler={handleSetChange}/>
      <SegmentDisplayer segments={segmentSetA}/>
    </div>
  );
}

function SegmentSelector( { changeHandler }) {
  return (
    <Form.Group>
      <Form.Label>Set A</Form.Label>
      <Form.Control type="text" onChange={e => changeHandler(e.target.value)}/>
    </Form.Group>
  )
}

function getCommonFeatures(segments) {
  let commonFeatureValues = {};
  for (feature of features) {
    const values = new Set();
    for (segment of segments) {
      values.add(distinctiveFeatures[segment][feature])
    }
    if (set.size == 1) {
      commonFeatureValues[feature] = values.values().next().value;
    }
  }
}

function SegmentDisplayer( { segments }) {
  return (
    <div>
      {segments && segments.join(",")}
    </div>
  )
}

export default App;
