import logo from './logo.svg';
import Form from 'react-bootstrap/Form';
import { useState, React} from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const features = [
  "dorsal",
  "voice",
  "continuant",
]

const distinctiveFeatures = {
  "k" : {
    "dorsal" : "+",
    "voice" : "-",
    "continuant" : "-",
  },
  "d" : {
    "dorsal" : "-",
    "voice" : "+",
    "continuant" : "-",
  }
}

function App() {
  const [segmentSetA, setSegmentSetA] = useState([]);
  function handleSetChange(setInputText) {
    // disregard empty strings or segments not accounted for
    let validSegments = [];
    for (let segment of setInputText.split(",")) {
      if (segment) {
        validSegments.push(segment);
      }
    }
    setSegmentSetA(validSegments);
  }
  return (
    <div className="App">
      <SegmentSelector changeHandler={handleSetChange}/>
      <SegmentDisplayer segments={segmentSetA}/>
    </div>
  );
}

function SegmentSelector( { changeHandler }) {
  return (
    <Form.Group>
      <Form.Label>Set A</Form.Label>
      <Form.Control type="text" onChange={e => changeHandler(e.target.value)}/>
    </Form.Group>
  )
}

function getCommonFeatures(segments) {
  let commonFeatureValues = {};
  for (let feature of features) {
    const values = new Set();
    for (let segment of segments) {
      values.add(distinctiveFeatures[segment][feature])
    }
    if (values.size == 1) {
      commonFeatureValues[feature] = values.values().next().value;
    }
  }
  return commonFeatureValues;
}

function SegmentDisplayer( { segments }) {
  if (!segments) {
    return (
      <div>
        No segments selected yet.
      </div>
    )
  }
  const commonFeatureValues = getCommonFeatures(segments);
  return (
    <div>
      Common features include:
      {
        Object.keys(commonFeatureValues).map(k => commonFeatureValues[k] + k)
      }
    </div>
  )
}

export default App;
